import Vue from 'vue'
import Router from 'vue-router'

// Importação de componentes

import sobre from '@/components/sobre.vue'

Vue.use(Router)

export default new Router({
    mode: 'history',
    routes: [
        {
            path:'/sobre',
            name:'sobre',
            component: sobre
        }
    ]
})
